mapper.select:
select no, dong, aptname, buildyear, jibun, lat, lng 
from houseinfo
where dong like concat(#{dongName}, '%');

select no as houseNo, house_no, dong as dongName, aptname, dealamount as dealAmount, dealyear as dealYear, dealmonth as dealMonth, dealday as dealDay, area, floor
from housedeal 
where house_no=#{houseNo}

select h.register_id, h.no, h.comp_seq, h.title, h.content, h.deal_amount, h.floor, h.area, h.direction, h.type, h.fee, h.room, h.bathroom
from house_ongoing h, company c
where h.comp_seq = c.comp_seq
and h.no = #{houseNo}
order by h.register_id desc
limit #{limit} offset #{offset}

select count(*)
from house_ongoing
where no = #{houseNo}
